 // export default Navbar;
+import { useState } from 'react';
+import { Link } from 'react-router-dom';
+import { Search, Menu, X, User } from 'lucide-react';
+
+export const Navbar = () => {
+  const [isMenuOpen, setIsMenuOpen] = useState(false);
+  
+  const navItems = [
+    ['Home', '/'],
+    ['Calculator', '/book-price-calculator'],
+    ['Pricing', '/pricing'],
+    ['Services', '/services'],
+    ['Contact', '/contact'],
+  ];
+
+  return (
+    <nav className="bg-[#FFE7C8] shadow-md">
+      <div className="container mx-auto px-4">
+        <div className="flex justify-between items-center h-16">
+          {/* Desktop Navigation */}
+          <div className="hidden md:flex items-center gap-8">
+            {navItems.map(([label, path]) => (
+              <Link
+                key={path}
+                to={path}
+                className="text-[#434343] hover:text-[#FF6100] px-3 py-2 rounded-md transition-colors font-medium"
+              >
+                {label}
+              </Link>
+            ))}
+          </div>
+
+          {/* Auth Section */}
+          <div className="flex items-center gap-4">
+            <button className="p-2 text-[#434343] hover:text-[#FF6100] rounded-full transition-colors">
+              <Search size={22} />
+            </button>
+            <Link
+              to="/login"
+              className="hidden md:flex items-center gap-2 px-5 py-2 bg-[#FF6100] hover:bg-[#FEA758] text-white rounded-full transition-colors"
+            >
+              <User size={18} />
+              <span>Login</span>
+            </Link>
+            
+            {/* Mobile Menu Button */}
+            <button
+              className="md:hidden p-2 text-[#434343]"
+              onClick={() => setIsMenuOpen(!isMenuOpen)}
+            >
+              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
+            </button>
+          </div>
+        </div>
+
+        {/* Mobile Menu */}
+        {isMenuOpen && (
+          <div className="md:hidden absolute w-full bg-white/95 backdrop-blur-sm shadow-xl left-0 px-4">
+            <div className="flex flex-col gap-2 py-4">
+              {navItems.map(([label, path]) => (
+                <Link
+                  key={path}
+                  to={path}
+                  className="text-[#434343] hover:text-[#FF6100] px-4 py-2 rounded-lg transition-colors"
+                  onClick={() => setIsMenuOpen(false)}
+                >
+                  {label}
+                </Link>
+              ))}
+              <Link
+                to="/login"
+                className="mt-4 px-4 py-2 bg-[#FF6100] hover:bg-[#FEA758] text-white rounded-full text-center"
+              >
+                Login
+              </Link>
+            </div>
+          </div>
+        )}
+      </div>
+    </nav>
+  );
+};

