import { useState } from 'react';
import { Menu, X } from 'lucide-react';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <nav className="fixed w-full top-0 z-50 bg-white/95 backdrop-blur shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <div className="flex-shrink-0">
            <span className="text-2xl font-bold text-orange-500">
              OrangeBooks
            </span>
          </div>

          {/* Desktop Menu */}
          <div className="hidden md:flex items-center space-x-8">
            <a href="#" className="text-gray-600 hover:text-orange-500 transition-colors">
              Publishing
            </a>
            <a href="#" className="text-gray-600 hover:text-orange-500 transition-colors">
              Resources
            </a>
            <a href="#" className="text-gray-600 hover:text-orange-500 transition-colors">
              About
            </a>
            <a href="#" className="text-gray-600 hover:text-orange-500 transition-colors">
              Contact
            </a>
            <button className="bg-orange-500 text-white px-6 py-2 rounded-lg hover:bg-orange-600 transition-colors">
              Get Started
            </button>
          </div>

          {/* Mobile Menu Button */}
          <div className="md:hidden">
            <button 
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="text-gray-600 hover:text-orange-500"
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden absolute w-full bg-white shadow-lg py-4">
            <div className="px-4 space-y-4">
              <a href="#" className="block text-gray-600 hover:text-orange-500">
                Publishing
              </a>
              <a href="#" className="block text-gray-600 hover:text-orange-500">
                Resources
              </a>
              <a href="#" className="block text-gray-600 hover:text-orange-500">
                About
              </a>
              <a href="#" className="block text-gray-600 hover:text-orange-500">
                Contact
              </a>
              <button className="w-full bg-orange-500 text-white px-6 py-2 rounded-lg hover:bg-orange-600">
                Get Started
              </button>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

