  return (
    <div>
      <Navbar />

      {/* Add top padding to your main content to account for fixed navbar */}
      <main className="pt-16"> {/* Adjust padding-top based on your navbar height */}
        {/* Your page content (hero section, etc) */}
        <div className="min-h-screen">
          {/* Page content here */}
        </div>
      </main>
    </div>
  );
};
